css:
.textcenter{
        text-align:center;
}
.borderclass{
    border-bottom: 1px solid dodgerblue;
}
table {
    width:100%;
    text-align:left;
    border-collapse: collapse;
}

table, td, th {
    /*width:50%;*/
    border: 1px solid dodgerblue;
    font-size:10px;
}
.print-format{
    padding: 12px !important;
    margin-bottom: 10px;
    margin-top:0px;
}
footer{
  clear: both;
position: relative;
  bottom:0;
  width:100%;
  text-align:center;
}

html:
{% set address %}{% endset %}
{% if doc.billing_address %}
    {%- set address=frappe.get_doc("Address",doc.billing_address) -%}
{% endif %}

{% set b_address %}{% endset %}
{% if doc.supplier_address %}
    {%- set b_address=frappe.get_doc("Address",doc.supplier_address) -%}
{% endif %}

{% set s_address %}{% endset %}
{% if doc.shipping_address %}
    {% set s_address=frappe.get_doc("Address",doc.shipping_address) %}
{% endif %}

{% set disc_col=[] %}
{% for disc_per in doc.items %}
    {% if disc_per.discount_percentage %}
        {% set disc_col = disc_col.append(disc_per.discount_percentage) %}
    {% endif %}
{% endfor %}

{% set so %}{% endset %}
{%for i in doc.items%}
{% if i.sales_order %}
    {%- set so=frappe.get_doc("Sales Order",i.sales_order) -%}
{% endif %}
{%endfor%}

{% set bd %}{% endset %}
{% if doc.company %}
    {% set bd=frappe.get_doc("Bank Account",{"company":doc.company,"is_default":1}) %}
{% endif %}

{% if doc.additional_discount_percentage %}
    {% set rowspan = doc.taxes|length + 3 %}
{% else %}
    {% set rowspan = doc.taxes|length + 2 %}
{% endif %}
{% set company_upi %}{% endset %}
{% if doc.company %}
    {% set company_upi = frappe.get_doc("Company",doc.company) %}
{% endif %}
<!DOCTYPE HTML>
<table>
    <tr>
        <td>
            {% if letter_head %}
                {{ letter_head }}
            {% endif %}
        </td>
        <tr>
            <td style="width: 100%;">
                <div class="col-xs-24">
                        <b>{{ _("Address") }}:</b>&nbsp; <span style="font-size: 10px">
                            {{ s_address.address_title }},
                            {{ s_address.address_line1 }}
                            {{ s_address.address_line2 }},
                            {{ s_address.city }}
                       </span>
                    </div>
            </td>
    </tr>
</table>
<table>
    <tr>
        <td style="text-align: center; font-size: 16px;"><b>PURCHASE VOUCHER</b></td>
    </tr>
</table>
<div style="width:100%;font-size: 15px;">
    <table>
        <tr>
            <td style="width: 50%;">
                <div class="row">
                    <div class="col-xs-7">
                        <b>{{ _("PI No.") }}:</b>&nbsp; <span style="font-size: 10px"><b>
                        {{ doc.name }}</b></span>
                    </div>
                    <div class="col-xs-5">
                        <b>{{ _("PI Date") }}:</b>&nbsp; {{ doc.get_formatted("posting_date") }}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-7">
                        <b>{{ _("PO No.") }}:</b>&nbsp;
                        {%for i in doc.items%}
                        {%if i.idx==1%}
                            {{i.purchase_order}}
                        {% endif %}
                        {% endfor %}
                    </div>
                    <div class="col-xs-5">
                        <b>{{ _("PO Date") }}:</b>&nbsp;
                        {% if doc.supplier_quotation_date %}
                            {{doc.get_formatted("supplier_quotation_date")}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-7">
                        <b>{{ _("Purchase Receipt No.") }}:</b>&nbsp;
                        {%for i in doc.items%}
                        {%if i.idx==1%}
                            {{i.purchase_receipt}}
                        {% endif %}
                        {% endfor %}
                    </div>
                    <div class="col-xs-5">
                        <b>{{ _("Purchase Receipt Date") }}:</b>&nbsp;
                        {% if doc.supplier_quotation_date %}
                            {{doc.get_formatted("supplier_quotation_date")}}
                        {% endif %}
                    </div>
                </div>
                 <div class="row">
                    <div class="col-xs-7">
                        <b>{{ _("Supp Inv No.") }}:</b>&nbsp;
                         {% if doc.bill_no %}
                            {{doc.bill_no}}
                        {% endif %}
                    </div>
                    <div class="col-xs-5">
                        <b>{{ _(" Date") }}:</b>&nbsp;
                        {% if doc.bill_date %}
                            {{doc.get_formatted("bill_date")}}
                        {% endif %}
                    </div>
                </div>

            </td>
            <td style="width: 50%;">
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Transporter Name") }}: </b>&nbsp;
                        {% if doc.transporter_name %} 
                            {{ doc.transporter_name }} 
                        {% endif %}
                    </div>

                </div>
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Destination") }}:</b>&nbsp;
                        {% if doc.destination %} 
                            {{doc.destination}} 
                        {% endif %}
                    </div>
                </div>

                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Freight") }}: </b>&nbsp; 
                        {% if doc.freight %} 
                            {{doc.freight}} 
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Insurance") }}:</b>&nbsp; 
                        {% if doc.insurance %} 
                            {{doc.insurance}} 
                        {% endif %}                       
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Due Date") }}: </b>&nbsp; 
                        {% if doc.due_date %} 
                            {{doc.due_date}} 
                        {% endif %}
                    </div>
                </div>
            </td>
        </tr>
</table>

<table>
        <tr>
            <td class="text-center" style="width: 50%;">
                <b>{{ _("Details of Supplier")}}</b>
            </td>
            <td class="text-center" style="width: 50%;">
                <b>{{ _("Details of Consignee")}}</b>
            </td>
        </tr>
        <tr style="width: 50%;">

            <td>
                <b>{{ _("Supplier Name") }}:&nbsp;
                {% if doc.supplier_name %}
                    {{doc.supplier_name}}
                {% endif %} </b><br>
                <b>{{ _("Address") }}:</b>&nbsp; 
                {% if b_address.address_line1 %} 
                    {{b_address.address_line1}}, 
                {% endif %}<br>
                {% if b_address.address_line2 %} 
                    {{b_address.address_line2}} ,
                {% endif %}<br>
                {% if b_address.city %} 
                    {{b_address.city}} 
                {% endif %}
                {% if b_address.pincode %} 
                    -{{b_address.pincode}}, 
                {% endif %}
                {% if b_address.gst_state %} 
                    {{b_address.gst_state}}, 
                {% endif %}
                {% if b_address.country %} 
                    {{b_address.country}}.
                {% endif %}<br>

                <b>Contact Person:</b>&nbsp;
                {% if doc.contact_display %}
                    {{doc.contact_display}}&nbsp;&nbsp;&nbsp;
                {% endif %}
                <b>M.No.:</b>&nbsp;
                {% if doc.contact_mobile %}
                    {{doc.contact_mobile}}
                {% endif %}<br>
                <b>Email:</b>&nbsp;
                {% if doc.contact_email %}
                    {{doc.contact_email}} 
                {% endif %}<br>
                <b>{{ _("GSTIN") }}:</b>&nbsp;
                {% if b_address.gstin %}
                    {{b_address.gstin}}
                {% endif %}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                <b>{{ _("State Code") }}:</b>&nbsp;
                {% if b_address.gst_state_number %}
                    {{b_address.gst_state_number}} 
                {% endif %}
            </td>
            <td style="width: 50%;">
                <b>{{ _("Name") }}:&nbsp;
                {% if s_address.address_line1 %}
                        {{s_address.address_title}}
                    {% endif %}
                </b><br>
                <b>{{ _("Address") }}:</b>&nbsp; 
                    {% if s_address.address_line1 %} 
                        {{s_address.address_line1}}, 
                    {% endif %}
                <br>
                {% if s_address.address_line2 %} 
                    {{s_address.address_line2}}, 
                {% endif %}<br>
                {% if s_address.city %} 
                    {{s_address.city}} 
                {% endif %}
                {% if s_address.pincode %} 
                    -{{s_address.pincode}}, 
                {% endif %}
                {% if s_address.gst_state %} 
                    {{s_address.gst_state}}, 
                {% endif %}
                {% if s_address.country %} 
                    {{s_address.country}}. 
                {% endif %}<br>

                <b>M.No.:</b>&nbsp;
                {% if s_address.phone %}
                    {{s_address.phone}}
                {% endif %}<br>
                <b>Email:</b>&nbsp;
                {% if s_address.email_id %}
                    {{s_address.email_id}} 
                {% endif %}<br>
                <b>{{ _("GSTIN") }}:</b>&nbsp;
                {% if s_address.gstin %}
                    {{s_address.gstin}}
                {% endif %}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                <b>{{ _("State Code") }}:</b>&nbsp;
                {% if s_address.gst_state_number %}
                    {{s_address.gst_state_number}}
                {% endif %}
            </td>
        </tr>
    </table>
    <table style="margin-top: 2px;">
        <tr>
            <th><b>{{ _("Sr No.") }}</b></th>
            <th><b>{{ _("Item Name") }}</b></th>
            <th><b>{{ _("HSN") }}</b></th>
            <th><b>{{ _("Batch No.") }}</b></th>
            <th><b>{{ _("MFG Date") }}</b></th>
            <th><b>{{ _("EXP Date") }}</b></th>
            <th><b>{{ _("Qty Packing") }}</b></th>
            <th><b>{{ _("Other Qty") }}</b></th>
            <th><b>{{ _("Rate Per Unit") }}</b></th>
            <th><b>{{ _("Discount") }}</b></th>
            <th><b>{{ _("Taxes") }}</b></th>

            <th><b>{{ _("Amount") }}</b></th>
        </tr>
        {%- for item in doc.items -%}
        {% set batch %}{% endset %}
        {% if item.batch_no %}
            {% set batch = frappe.get_doc("Batch",item.batch_no) %}
        {% endif %}
        <tr>
            <td style="text-align: center;">{{ loop.index }}</td>
            <td>{{ item.item_name }}
                {% if item.customer_item_code %}
                    <br><span style="font-size: 9px"><b>Cust. Item Name</b>&nbsp;
                    {{ item.customer_item_code }}</span>
                {% endif %}
            </td>
            <td>
                {% if item.gst_hsn_code %}
                    {{ item.gst_hsn_code }}
                {% endif %}
            </td>
             <td>
                {% if item.batch_no %}
                    {{ item.batch_no }}
                {% endif %}
            </td>
            <td style="text-align: center;width:10%"> 
                {% if batch.manufacturing_date %}
                    {{ batch.get_formatted("manufacturing_date") }}
                {% endif %}
            </td>
            <td style="text-align: center;width:10%;">
                 {% if batch.expiry_date %}
                    {{ batch.get_formatted("expiry_date") }}
                {% endif %}
            </td>
            <td style="text-align: center;">

                {% if item.qty %}
                    {{item.qty}}
                {% endif %}&nbsp;
                {% if item.uom %}
                    {{item.uom}}
                {% endif %}&nbsp;
            </td>
            <td style="text-align: center;">

               {%set a=frappe.get_doc("Item",item.item_code)%}
                 {%for j in a.uoms%}
                 {%if j.uom=="Case"%}
                    {{item.qty/j.conversion_factor}}&nbsp; Case
                {%endif%}
                {% endfor %}
            </td>
            <td style="text-align: center;">

               {% if item.stock_uom_rate %}
                {{ item.get_formatted("stock_uom_rate") }}
            {% endif %}

            </td> 
            <td style="text-align: center;">

                    {{doc.additional_discount_percentage}}%


            </td> 
            <td style="text-align: right;">
                {%set c=frappe.get_doc("Item Tax Template",item.item_tax_template)%}
                {{c.percentage}}%

           </td>

            <td style="text-align: right;width:12%;">
            {% if item.amount %}
                {{ item.get_formatted("amount") }}
            {% endif %}</td>
        </tr>
        {%- endfor -%}
        <tr>
            <th></th>
            <th></th>
            <th></th>
            <th></th>
            <th></th>
            <th style="text-align: center;"><b>{{ _("Total") }}</b></th>
            <th style="text-align: center;"><b>{{doc.get_formatted("total_qty")}}</b></th>
            <th></th>
            <th></th>
            <th></th>
            <th></th>
            <th style="text-align: right;"><b>{{ doc.get_formatted("total") }}</b></th>
        </tr>
    </table>


    <table style="margin-top: 2px;">
        <tr>
            <td rowspan ="{{ rowspan }}" style ="width:57%;">
                <b>Total Amount in Words:</b><br>
                {% if doc.in_words %}
                    {{ doc.in_words }}
                {% endif %}
                <hr style="margin-bottom: 2px;margin-top: 2px;">
                <b>Payment Terms:</b>&nbsp;
                {% if doc.payment_terms_template %}
                    {{doc.payment_terms_template}}
                {% endif %}<br>
                <b>{{ _("E-Waybill Number") }}:</b>&nbsp;
                {% if doc.ewaybill %}
                    {{doc.ewaybill}}
                {% endif %} &nbsp;&nbsp;
                <b>{{ _("E-Waybill Date ") }}:</b>&nbsp; 
                {% if doc.e_way_bill_date %} 
                    {{doc.get_formatted("e_way_bill_date")}}
                {% endif %}<br>

                <b>{{ _("IRN No.") }}:</b>&nbsp; 
                {% if doc.irn %} 
                    {{doc.irn}}
                {% endif %}
            </td>
            <td style ="width:28%;" class ="text-right">
                <b>Total Amount:</b>
            </td>
            <td style ="width:11%;" class ="text-right">{{ doc.get_formatted("total") }}</td>
        </tr>
        {% if doc.additional_discount_percentage %}
            <tr>
                <td style ="width:28%;" class ="text-right"><b>Additional Disc. on {{doc.apply_discount_on}}: &nbsp;{{doc.additional_discount_percentage}}%</b></td>
                <td style ="width:11%;" class ="text-right"> -&nbsp;{{ doc.get_formatted("discount_amount")}}</td>
            </tr>
        {% endif %}
        <tr>
            <td style ="width:28%;" class ="text-right"><b>Taxable Value:</b></td>
            <td style ="width:11%;" class ="text-right"> &nbsp;
            {{ doc.get_formatted("net_total")}}</td>
        </tr>
        {%- for tax in doc.taxes -%}
        <tr>
            <td style ="width:28%;" class ="text-right">
                <b>{{ tax.description }}</b>
            </td>
            <td style ="width:11%;" class ="text-right">
                {{ tax.get_formatted("tax_amount") }}
            </td>
        </tr>
        {%- endfor -%}
        <tr>
            <td style ="width:57%;">

                <b>Account Name :</b>&nbsp;
                {% if bd.account_name %}
                    {{bd.account_name}}
                {% endif %}<br>
            </td>
            <td style ="width:28%;" class ="text-right"><b>Total:</b></td>
            <td style ="width:11%;" class ="text-right"> {{ doc.get_formatted("grand_total")}}</td>
        </tr>
        <tr>

            <td style ="width:57%;"><b>Bank Name :</b>&nbsp;
                {% if bd.bank %}
                    {{bd.bank}}
                {% endif %}
                &nbsp;&nbsp;&nbsp;<b>Account Number :</b>&nbsp;
                {% if bd.bank_account_no %}
                    {{bd.bank_account_no}}
                {% endif %}</td>
            <td style ="width:28%;" class ="text-right"><b>Round Off Amount :</b></td>
            <td style ="width:11%;" class ="text-right"> {{ doc.get_formatted("rounding_adjustment")}}</td>
        </tr>
        <tr>

            <td style ="width:57%;"><b>IFSC Code :</b>&nbsp;
                {% if bd.ifsc_code %}
                    {{bd.ifsc_code}}
                {% endif %}
                &nbsp;&nbsp;&nbsp;<b>Branch Code :</b>&nbsp;
                {% if bd.branch_code %}
                    {{bd.branch_code}}
                {% endif %}<br></td>
            <td style ="width:28%;" class ="text-right"><b>Invoice Value :</b></td>
            <td style ="width:11%;" class ="text-right"> {{ doc.get_formatted("rounded_total")}}</td>
        </tr>
    </table>
    {% if doc.other_charges_calculation %}
    <div style="margin-top: -15px; margin-bottom: -15px;">{{ doc.other_charges_calculation }} </div>
    {% endif %}
    <table style="margin-top: 0px;">
        <tr>
            <td style ="width:50%;" rowspan =""><b>Terms and Conditions :</b><br>
                {% if doc.terms %}
                    <span style="font-size: 7px;">{{ doc.terms }}</span>
                {% endif %}                
            </td>
            {% if company_upi.upi_qr_code_image %}
                <td style="width: 20%">
                    <div><img src="{% if company_upi.upi_qr_code_image %}{{company_upi.upi_qr_code_image}}{% endif %}" width="130" height="130"></div>
                    <div class="text-center"><b>UPI ID:{% if company_upi.upi_id %}{{ company_upi.upi_id }}{% endif %}</b></div>
                </td>
            {% endif %}
            <td style="font-size:10px; width: 30%"><br>
                <p class="text-center">For <b>
                {% if doc.company %}
                    {{doc.company}}
                {% endif %}</b><br></p><br><br><br><br><br><br><br><br>
                <p class ="text-center"><b>Authorised Signatory</b></p>
            </td>
        </tr>
    </table>
</div>
